// Split the string
def mystring = "robert"

@inline
def split_string[S] = n, substring[S,n,n]
	from n in range[1,num_chars[S],1]

def soundex_replace =
	{ ({"b"; "f"; "p"; "v";}, "1");
      ({"c"; "g"; "j"; "k"; "q"; "s"; "x"; "z";}, "2");
      ({"d";"t";}, "3");
      ("l", "4");
      ({"m";"n";}, "5");
      ("r", "6"); }

def mychars(n,v) = split_string[mystring](n,v) and
	not n = 1
def mysoundex = 1, uppercase[substring[mystring,1,1]]
def mysoundex[n] = soundex_replace[ mychars[n] ]

def soundex = substring[ string_join["", mysoundex], 1, 4]

def output = soundex

def mystring = "Rubin"

// Split the string
@inline
def split_string[S] = n, substring[S,n,n]
	from n in range[1,num_chars[S],1]

// Sound replacement rules
def soundex_replace =
	{ ({"b"; "f"; "p"; "v";}, "1");
      ({"c"; "g"; "j"; "k"; "q"; "s"; "x"; "z";}, "2");
      ({"d";"t";}, "3");
      ("l", "4");
      ({"m";"n";}, "5");
      ("r", "6");
      ({"a"; "e"; "i"; "o"; "u";}, "0"); }

// Keep first letter
def mysoundex = 0, uppercase[substring[mystring,1,1]]
// Replace letters with sound encoding, drop letters without replacements
def mysoundex[n] = soundex_replace[ split_string[ lowercase[mystring]][n] ]

// Remaining rules
def mysoundex_filter(n,v) =
	mysoundex(n,v) and
    not mysoundex(n-1,v) and
	not mysoundex(n,"0") and
	not n=1

def soundex = substring[ concat[ string_join["", mysoundex_filter], "000"], 1, 4 ]

def output = soundex

// Split the string
@inline
def split_string[S] = n, substring[S,n,n]
	from n in range[1,num_chars[S],1]

// Sound replacement rules
def soundex_replace =
	{ ({"b"; "f"; "p"; "v";}, "1");
      ({"c"; "g"; "j"; "k"; "q"; "s"; "x"; "z";}, "2");
      ({"d";"t";}, "3");
      ("l", "4");
      ({"m";"n";}, "5");
      ("r", "6");
      ({"a"; "e"; "i"; "o"; "u";}, "0"); }

// Keep first letter
@inline
def soundex_pre[S] = 0, uppercase[substring[S,1,1]]
// Replace letters with sound encoding, drop letters without replacements
@inline
def soundex_pre[S][n] = soundex_replace[ split_string[ lowercase[S]][n] ]

// Remaining rules
@inline
def soundex_filter[S](n,v) =
	soundex_pre[S](n,v) and
    not soundex_pre[S](n-1,v) and // No adjacent numbers the same
	not soundex_pre[S](n,"0") and // No vowels
	not n=1 // Drop the number for the first letter

// Join string and pad with zeros
@inline
def soundex[S] = substring[ concat[ string_join["", soundex_filter[S]], "000"], 1, 4 ]

def mystring = "Tymczak"
def output = soundex[mystring] // T522

def test_strings = 
	{ ("Robert", "R163");
      ("Rupert", "R163");
      ("Rubin", "R150");
      ("Ashcroft", "A261");
      ("Ashcraft", "A261");
      ("Tymczak", "T522");
      ("Pfister", "P236");
      ("Honeyman", "H555"); }

def output = name, answer, result
	from name, answer, result where
	test_strings(name, answer) and
	result = soundex[name]
